const _this = this;
const fs = require('fs');
var someArray = [];
var someNum = 0;
var globalData;
var interval;

fs.readFile('./data/globalVars.json', function read(err, data) {
    if (err) {
        throw err;
    }
    var savedVars = JSON.parse(data);

    if (savedVars.hasOwnProperty('tempData')) {
        var newThing = savedVars.tempData.replace(/[\\]/g,'');
        try {
            _this.storeValue(JSON.parse(newThing), 3, 'reactionRoleInf', cache);
            console.log('Saved reactionRoleInf');
        } catch (e) {
            console.log('Invalid JSON - Saved Vars')
        }
    }

    if(fs.existsSync('./resources/serverRoleMenu.json')) {
        fs.readFile('./resources/serverRoleMenu.json', async function read(err, data) {
            if (err) {
                throw err;
            }
            var DataToLoop = JSON.parse(data);
            globalData = DataToLoop;
            for (var key in DataToLoop) {
                if (DataToLoop.hasOwnProperty(key)) {
                    await someArray.push(DataToLoop[key]);
                }
            }
            interval = setInterval(fetchMessages, 2000);
        })

    } else {
        fs.writeFile("./resources/serverRoleMenu.json", "{}", function(err) {
            if(err) {
                return console.log(err);
            }
        });
    }
});

function fetchMessages() {
 if (someNum != someArray.length) {
     var channelToFetch = client.channels.get(someArray[someNum]);
     if (channelToFetch != null) {
        channelToFetch.fetchMessages({ limit: 100 });
        console.log(`Fetched messages in channel id ${someArray[someNum]}`);
        someNum = someNum + 1;
     } else {
        delete globalData[someArray[someNum]];
        fs.writeFile("./resources/serverRoleMenu.json", JSON.stringify(globalData), function(err) {
            if(err) {
                return console.log(err);
            }
        }); 
     }
 } else {
    console.log('Finished fetching messages from all logged servers.')
    clearInterval(interval);
 }
}

